package designpattern.agent;/** * <p> * * </p> * * @author lipeilong * @time 2020/9/10 17:19 * @description */public class ProxyImage implements Image{    private RealImage realImage;    private String fileName;    public ProxyImage(String fileName){        this.fileName = fileName;    }    @Override    public void display() {        if(realImage == null){            realImage = new RealImage(fileName);        }        realImage.display();    }}