package designpattern.agent;/** * <p> * * </p> * * @author lipeilong * @time 2020/9/10 17:07 * @description */public class RealImage implements Image {    private String fileName;    public RealImage(String fileName){        this.fileName = fileName;        loadFromDisk(fileName);    }    @Override    public void display() {        System.out.println("Displaying " + fileName);    }    private void loadFromDisk(String fileName){        System.out.println("Loading " + fileName);    }}