package designpattern.templete;/** * <p> * step2:创建扩展了上述类的实体类。 * </p> * * @author lipeilong * @time 2020/9/10 17:30 * @description step2:创建扩展了上述类的实体类。 */public class Cricket extends Game {    @Override    void endPlay() {        System.out.println("Cricket Game Finished!");    }    @Override    void initialize() {        System.out.println("Cricket Game Initialized! Start playing.");    }    @Override    void startPlay() {        System.out.println("Cricket Game Started. Enjoy the game!");    }}