package designpattern.templete;/** * <p> * step2 * </p> * * @author lipeilong * @time 2020/9/10 17:30 * @description step2 */public class Football extends Game {    @Override    void endPlay() {        System.out.println("Football Game Finished!");    }    @Override    void initialize() {        System.out.println("Football Game Initialized! Start playing.");    }    @Override    void startPlay() {        System.out.println("Football Game Started. Enjoy the game!");    }}