package method;import java.util.ArrayList;import java.util.Set;import java.util.TreeSet;/** * 输入一个字符串,按字典序打印出该字符串中字符的所有排列。 * 例如输入字符串abc,则打印出由字符a,b,c所能排列出来的所有字符串abc,acb,bac,bca,cab和cba。 * @author lipeilong */public class Permutation {    Set<String> strings = new TreeSet<>();    public ArrayList<String> Permutation(String str) {        if (str.length() < 1) {            return new ArrayList<String>();        }        StringBuffer string = new StringBuffer(str);        helpPer(string, 0);        ArrayList<String> result = new ArrayList<>(strings);        return result;    }    public void helpPer(StringBuffer str, int index) {        if (str.length() == index + 1) {            strings.add(str.toString());            return;        }        for (int j = index; j < str.length(); j++) {            char temp = str.charAt(j);            str.setCharAt(j, str.charAt(index));            str.setCharAt(index, temp);            helpPer(str, index + 1);            str.setCharAt(index, str.charAt(j));            str.setCharAt(j, temp);        }    }}