package method;import java.util.Scanner;public class CountingBinaryTrees {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int n = sc.nextInt();        if (n>30000){            System.out.println("error");            return;        }        System.out.println(cal(n));    }    static int cal(int n){        if (n<=0){            return 0;        }        int result = ( 4*n -2)/(n+1) + cal(n-1);        return result;    }}