package method;import java.util.*;/** * 输入一个递增排序的数组和一个数字S，在数组中查找两个数，使得他们的和正好是S， * 如果有多对数字的和等于S，输出两个数的乘积最小的。 */public class FindNumbersWithSum {    public static void main(String[] args) {        int array[] = {1,2,4,7,11,15};        System.out.println(FindNumbersWithSum(array,15));    }    public static ArrayList<Integer> FindNumbersWithSum(int[] array, int sum) {        if (array.length <= 1) {            return new ArrayList<>();        }        ArrayList<Integer> arrayList = new ArrayList<>();        for (int i = 0; i < array.length; i++) {            arrayList.add(array[i]);        }        Set<Integer> temp = new TreeSet<>();        for (int i : array) {            int i2 = sum - i;            if (!arrayList.contains(i2)) {                continue;            }            if (temp.add(i)){                break;            }        }        Iterator iterator = temp.iterator();        if (iterator.hasNext()){            Integer small = (Integer) iterator.next();            return new ArrayList<>(Arrays.asList(small, (sum - small)));        }        return new ArrayList<>();    }}