package method;import java.util.HashMap;import java.util.Map;/** * 一个整型数组里除了两个数字之外，其他的数字都出现了两次。请写程序找出这两个只出现一次的数字。 * num1,num2分别为长度为1的数组。传出参数 * 将num1[0],num2[0]设置为返回结果 */public class FindNumsAppearOnce {    public void FindNumsAppearOnce(int[] array, int num1[], int num2[]) {        Map<Integer, Integer> map = new HashMap<>();        for (int i : array) {            if (map.containsKey(i)) {                map.put(i, 2);            } else {                map.put(i, 1);            }        }        for (int i : array) {            if (map.get(i) == 1) {                if (num1[0] == 0) {                    num1[0] = i;                } else {                    num2[0] = i;                }            }        }    }}