package method;public class IsBalanced_Solution {    public boolean IsBalanced_Solution(TreeNode root) {        if (root == null) {            return true;        }        return Math.abs(countSon(root.left) - countSon(root.right)) <= 1 &&                IsBalanced_Solution(root.left) &&                IsBalanced_Solution(root.right);    }    public int countSon(TreeNode root) {        if (root == null) {            return 0;        }        return 1 + Math.max(countSon(root.left), countSon(root.right));    }    public class TreeNode {        int val = 0;        TreeNode left = null;        TreeNode right = null;        public TreeNode(int val) {            this.val = val;        }    }}