package method;/** * <p> * 将一个字符串转换成一个整数，要求不能使用字符串转换整数的库函数。 数值为0或者字符串不是一个合法的数值则返回0 * </p> * * @author lipeilong * @time 2020/8/11 16:49 * @description 将一个字符串转换成一个整数，要求不能使用字符串转换整数的库函数。 数值为0或者字符串不是一个合法的数值则返回0 */public class StrToInt {    public static void main(String[] args) {        System.out.println(StrToInt("123"));    }    public static int StrToInt(String str) {        if (str.trim().equals("")) {            return 0;        }        int flag = 1;        StringBuffer stringBuffer = new StringBuffer(str);        if (stringBuffer.charAt(0) < '0') {            if (stringBuffer.charAt(0) == '-') {                flag = -1;                stringBuffer.deleteCharAt(0);            } else if (stringBuffer.charAt(0) == '+') {                stringBuffer.deleteCharAt(0);            } else {                return 0;            }        }        char[] c = stringBuffer.toString().toCharArray();        if (stringBuffer.length() == 0) {            return 0;        }        int result = stringBuffer.charAt(0) - '0';        for (int i = 1; i < c.length; i++) {            char value = c[i];            if (isNum(value)) {                result = result * 10 + value - '0';            } else {                return 0;            }        }        result = flag == 1 ? result : -result;        return result;    }    public static boolean isNum(char c) {        return c >= '0' && c <= '9';    }}