package method;import java.util.ArrayList;import java.util.Arrays;import java.util.List;import java.util.Stack;public class VerifySquenceOfBST {    public static void main(String[] args) {        int[] se = {2,4,3,8,11,10};        System.out.println(VerifySquenceOfBST(se));    }    public static boolean VerifySquenceOfBST(int[] sequence) {        if (sequence.length == 0) {            return false;        }        return isBST(sequence,0,sequence.length-1);    }    static boolean isBST(int[] sequence, int start, int end) {        if (start>=end) {            return true;        }        int i = start;        for (; i < end; i++) {            if (sequence[i] > sequence[end]) {                break;            }        }        for (int j = i;j<end;j++){            if (sequence[j]<sequence[end]){                return false;            }        }        return isBST(sequence,start,i-1)&&isBST(sequence,i,end-1);    }}