package method;/** * <p> * 翻转一棵二叉树。 * </p> * * @author lipeilong * @time 2020/9/16 17:40 * @description 翻转一棵二叉树。 */public class 翻转二叉树 {    public TreeNode invertTree(TreeNode root) {        if (root == null) {            return root;        }        invertTree(root.left);        invertTree(root.right);        TreeNode temp;        temp = root.left;        root.left = root.right;        root.right = temp;        return root;    }    public class TreeNode {        int val;        TreeNode left;        TreeNode right;        TreeNode(int x) {            val = x;        }    }}