package test;import java.util.*;public class Main {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int k = scanner.nextInt();        if (k < 1 || k > 100000) {            System.out.println("0");            return;        }        List<Integer> listA = new ArrayList<>(k);        for (int i = 0; i < k; i++) {            listA.add(scanner.nextInt());        }        List<Integer> listB = new ArrayList<>(k);        for (int i = 0; i < k; i++) {            listB.add(scanner.nextInt());        }        int n = scanner.nextInt();        List<Integer> outlook = new ArrayList<>(n);        for (int i = 0; i < n; i++) {            outlook.add(scanner.nextInt());        }        List<Integer> price = new ArrayList<>();        for (int i = 0; i < n; i++) {            price.add(scanner.nextInt());        }        if (k > n) {            System.out.println("0");            return;        }        List<Integer> resA = new ArrayList<>();        List<Integer> resB = new ArrayList<>();        cal(0, listA, 0, outlook, resA);        cal(0, listB, 0, price, resB);        for (Integer i : resA) {            for (Integer j : resB) {                if (i.equals(j)) {                    System.out.println(i + 1);                    return;                }            }        }        System.out.println(0);    }    private static void cal(int listAIndex, List<Integer> listA, int outlookIndex, List<Integer> outlook, List<Integer> res) {        if (listAIndex >= listA.size() - 1) {            res.add(outlookIndex);            return;        }        for (int i = outlookIndex; i < outlook.size() - listA.size() + 1; i++) {            if (outlook.get(i).equals(listA.get(listAIndex))) {                cal(listAIndex + 1, listA, i + 1, outlook, res);            }        }    }}